import React from 'react';
import productData from '../products.json';
import Product from './Product';
import { Link } from "react-router-dom";

class HomePage extends React.Component {

sortByPriceAsc() {
	this.setState(prevState => {
		this.state.products.sort((a, b) => (a.price - b.price))
	});
	}

	sortByPriceDesc() {
	this.setState(prevState => {
		this.state.products.sort((a, b) => (b.price - a.price))
	});
	}

render() {
	var products = productData.map(function(product) {
		return (
			<li key={product.id}>
				<Product data={product} />
			</li>
		)
});

	return (
			<div>
				<select>
				<option value={this.sortDescending}>Highest to Lowest</option>
				<option value={this.sortAscending}>Lowest to Highest</option>
				</select>	
				<Link to="/checkout">Checkout</Link> 
			{products}
						</div>

	);
	}
}


export default HomePage;
