import React from 'react';
import productData from '../products.json';

class HomePage extends React.Component {

constructor(props) {
	super(props)
	this.state = {
		value:"Add To Cart"
	}
}
addProduct() {
	alert("Item added");
	this.setState({
		value:"Remove from Cart"

	});
}
	render() {
		return (	
			productData.map((dataDetails) => {
			 return	(
			<div><h3>{dataDetails.name} {dataDetails.price}</h3>
			 <button onClick={this.addProduct}>{this.state.value}</button>
			 </div>
			 );
			})
		);
	}
}


export default HomePage;
