import React from 'react';
import productData from '../products.json';

class HomePage extends React.Component {

constructor(props) {
	super(props)
	this.state = {
		value:"Add To Cart"
	}
	this.addProduct = this.addProduct.bind(this);
	this.readData = this.readData.bind(this);
}

addProduct = (name) => {
	this.setState({
		value:"Remove from Cart"
	});
	sessionStorage.setItem('product',name);
}
readData  =() => {
 return productData.map((dataDetails,index) => {
   return (
     <div>
       <h3>
         {dataDetails.name} {dataDetails.price}
       </h3>
       <button onClick={this.addProduct}>{this.state.value} id ={index}</button>
     </div>
   );
 });
}

render() {
		return (	
		<div>
			{this.readData()}
			</div>
		);
	}
}


export default HomePage;
