import React from 'react';
import productData from '../products.json';
import Product from './Product';

class HomePage extends React.Component {
constructor(props) {
	super(props)
	this.state = {
		product:productData
	} 
	this.sortByPriceAsc = this.sortByPriceAsc.bind(this);
	this.sortByPriceDesc = this.sortByPriceDesc.bind(this);
	}

sortByPriceAsc() {
	this.setState(prevState => {
		this.state.product.sort((a, b) => (this.state.product.price - b.price))
	});
	}

	sortByPriceDesc() {
	this.setState(prevState => {
		this.state.productData.sort((a, b) => (b.price - a.price))
	});
	}

render() {
	var products = productData.map(function(product) {
		return (
			<li key={product.id}>
				<Product data={product} />
			</li>
		)
});

	return (
			<div>
				<button onClick={this.sortByPriceAsc}>
            Lowest to Highest	
            </button>
            <button onClick={this.sortByPriceDesc}>
            Highest to Lowest
            </button>
			{products}</div>

	);
	}
}


export default HomePage;
