import React from 'react';
import productData from '../products.json';

class HomePage extends React.Component {

constructor(props) {
	this.state = {
		value:"Add To Cart"		
	}
	this.addProduct = this.addProduct.bind(this);
	this.readData = this.readData.bind(this);
}
addProduct = (price) => {
	this.setState({
		value:"Remove from cart"
	})
	// sessionStorage.setItem('product',price);
}

readData() {
 	return productData.map((dataDetails,index) => {
		return (
			<div>
				<h3>
				{index}	{dataDetails.name} {dataDetails.price}
				</h3>
				<button onClick={this.addProduct(dataDetails.price)}>{this.state.value}</button>
			</div>
		);

	})
}


render() {
		return (	
		<div>
			  {this.readData()}
			</div>
		);
	}
}


export default HomePage;
