import React from 'react';

class Product extends React.Component {
    constructor(props){
        super(props);
        this.setState = {
            products: products	
        }
        this.addProduct = this.addProduct.bind(this);
    }

    addProduct = () => {
        if(!this.state.products.isInCart) {
            // add
            $.publish('cart.added', this.props.data);
          }
          else {
            // remove
            $.publish('cart.removed', this.props.data.id);
          }

        console.log(price);
        this.setState({
            value:"Remove from cart"
        })
        sessionStorage.setItem('product',price);

    }
    render() {
        // assign to props
        var data = this.props.data;

        return (
            <div>
              <h3>{data.name}</h3>
              <div className="product__price">{data.price}</div>
              <div className="product__button-wrap">
                <button onClick={this.addProduct}>
                  {data.isInCart ? 'Add to cart' : 'Remove from Cart'}
                </button>
              </div>
              </div>
        );

    }
};
export default Product;
