import React from 'react';

class Product extends React.Component {
    constructor(props){
        super(props);
        var data = this.props.data;
        this.setState = {
            products: data
        }
        this.addProduct = this.addProduct.bind(this);
    }

    addProduct = () => {
        if(!this.state.products.isInCart) {
            alert("Item added");
          }
          else {
            alert("Item Removed");
          }
          this.setState({
            isInCart: false
          });    
    }
    render() {
        var data = this.props.data;
        console.log(data)
        return (

            <h1>Home page</h1>
        );

    }
};
export default Product;
