import React from 'react';
import productData from '../products.json';

class HomePage extends React.Component {

constructor(props) {
	super(props)
	this.state = {
		value:"Add To Cart",
		products: productData		
	}
	this.addProduct = this.addProduct.bind(this);
	this.readData = this.readData.bind(this);
}
addProduct = (event) => {
	let ee = event.target.getAttribute('id');
	let price = sessionStorage.getItem('product');
	price = price + ee;
	console.log(price);
	this.setState({
		value:"Remove from cart"
	})
	sessionStorage.setItem('product',price);

}

readData() {
	console.log(this.state.products);
 	return this.state.products.map((dataDetails,index) => {
		return (
			<div>
				<h3>
				{index}	{dataDetails.name} {dataDetails.price}
				</h3>
				<button onClick={this.addProduct} id={dataDetails.price}>
				{this.state.products.is ? 'Remove' : 'Add to cart'}
				{this.state.value}</button>
			</div>
		);

	})
}


render() {
		return (	
		<div>
				{this.readData()}
			</div>

		);
	}
}


export default HomePage;
