import React from 'react';

class Product extends React.Component {

    addProduct = (event) => {
        let ee = event.target.getAttribute('id');
        let price = sessionStorage.getItem('product');
        price = price + ee;
        console.log(price);
        this.setState({
            value:"Remove from cart"
        })
        sessionStorage.setItem('product',price);

    }
    render() {
        // assign to props
        var data = this.props.data;

        return (
            <div>
              <h3>{data.name}</h3>
              <div className="product__price">{data.price} {data.currency}</div>
              <div className="product__button-wrap">
                <button className={this.state.added ? 'btn btn-danger' : 'btn btn-primary'} onClick={this.addToCart}>
                  {this.state.added ? 'Remove' : 'Add to cart'}
                </button>
              </div>
              </div>
        );

    }
};
export default Product;
